INSERT INTO public.faq (id, question, answer, changeddate, faqcategory_id) VALUES (6, 'O que é o Plano de Evacuação?', 'O Plano de Evacuação é uma parte do plano de emergência no qual estão indicados os caminhos de evacuação, zonas de segurança, regras de conduta das pessoas e a sucessão de acções a terem lugar durante a evacuação de um local, estabelecimento, recinto ou edifício, em caso de incêndio.', '2009-11-03 16:48:33.016000', 7);
INSERT INTO public.faq (id, question, answer, changeddate, faqcategory_id) VALUES (7, 'O que é o Plano de Prevenção?', 'O Plano de prevenção é um documento no qual estão indicados a organização e os procedimentos a adoptar, por uma entidade, para evitar a ocorrência de incêndios e para garantir a manutenção do nível de segurança decorrente das medidas de autoprotecção adoptadas e a preparação para fazer face a situações de emergência;', '2009-11-03 16:50:59.325000', 7);
INSERT INTO public.faq (id, question, answer, changeddate, faqcategory_id) VALUES (8, 'O que é uma Planta de Emergência?', 'A Planta de emergência é uma peça desenhada esquemática, referente a um dado espaço com a representação dos caminhos de evacuação e dos meios a utilizar em caso de incêndio, contendo ainda as instruções gerais de segurança aplicáveis a esse espaço. Deve estar conforme a NP 4386.', '2009-11-03 16:52:41.223000', 7);
INSERT INTO public.faq (id, question, answer, changeddate, faqcategory_id) VALUES (9, 'O que são os Registos de Segurança?', 'Os Registos de Segurança são um conjunto de documentos que contém os registos de ocorrências relevantes e de relatórios relacionados com a segurança contra incêndios.
INSERT INTO public.faq (id, question, answer, changeddate, faqcategory_id) VALUES (5, 'O que é o Plano de Emergência?', 'O Plano de Emergência é um documento no qual estão indicadas as medidas de autoprotecção a adoptar, por uma entidade, para fazer face a uma situação de incêndio nas instalações ocupadas por essa entidade, nomeadamente a organização, os meios humanos e materiais a envolver e os procedimentos a cumprir nessa situação. Contém o plano de actuação e o de evacuação.', '2009-11-03 16:56:17.987000', 7);