INSERT INTO public.helpsearchablecontent (id, searchcontent, titletosearch, titletoshow, filename, permissions) VALUES (20, 'a aplicacao para gerir as licencas do certitools esta disponivel no endereco:', 'administracao', 'Administração', 'administration', 'administrator');
