INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (928, 'Estabelece um quadro para a avaliação e gestão dos riscos de inundações, com o objectivo de reduzir as suas consequências prejudiciais', '115', '2010', '2010-10-22', 'avaliação gestão riscos inundações', 'Estabelece um quadro para a avaliação e gestão dos riscos de inundações, com o objectivo de reduzir as suas consequências prejudiciais, e transpõe a Directiva n.º 2007/60/CE, do Parlamento Europeu e do Conselho, de 23 de Outubro', '<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong></span></p>
<p>-&nbsp;Instituto da &Aacute;gua, I. P.</p>
<p>-&nbsp;Administra&ccedil;&otilde;es das Regi&otilde;es Hidrogr&aacute;ficas (ARH)</p>
<p>-&nbsp;Autoridade Nacional de Protec&ccedil;&atilde;o Civil (ANPC)</p>
<p>-&nbsp;Instituto Geogr&aacute;fico Portugu&ecirc;s (IGP)</p>
<p><span style="color: #003366"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></span></p>
<p>-&nbsp; Directiva n.&ordm; 2007/60/CE, do Parlamento Europeu e do Conselho, de 23 de Outubro</p>
<p><span style="color: #003366"><strong>DIPLOMAS ALTERADOS:</strong> </span>n/a</p>
<p><span style="color: #003366"><strong>DIPLOMAS REVOGADOS:</strong></span><span style="color: #333399"><strong> </strong></span>n/a</p>
<p><span style="color: #003366"><strong>DIPLOMAS REPUBLICADOS:</strong></span><strong> </strong>n/a</p>
<p><span style="color: #003366"><strong>ALTERA&Ccedil;&Otilde;ES A ESTE DIPLOMA:</strong> </span>n/a</p>
<p><span style="color: #003366"><strong>REVOGA&Ccedil;&Otilde;ES DE ARTIGOS DESTE DIPLOMA: </strong></span>n/a</p>', '<p style="text-align: justify; ">1 &mdash; As ARH, ouvida a CNGRI, podem decidir n&atilde;o efectuar&nbsp;a avalia&ccedil;&atilde;o preliminar dos riscos de inunda&ccedil;&otilde;es referida&nbsp;no artigo 5.&ordm; para as bacias hidrogr&aacute;ficas, as sub -bacias&nbsp;hidrogr&aacute;ficas ou as zonas costeiras em rela&ccedil;&atilde;o &agrave;s quais:<br />
a) J&aacute; tenha sido efectuada uma avalia&ccedil;&atilde;o cred&iacute;vel dos&nbsp;riscos que lhes permita concluir, antes de 22 de Dezembro&nbsp;de 2010, que existe um risco potencial significativo de&nbsp;inunda&ccedil;&otilde;es, ou que a sua concretiza&ccedil;&atilde;o se pode considerar&nbsp;prov&aacute;vel, e que, por esse motivo, se justifica a inclus&atilde;o<br />
imediata dessas zonas entre as zonas mencionadas no n.&ordm; 1&nbsp;do artigo 6.&ordm;; ou<br />
b) Tenham decidido, antes de 22 de Dezembro de 2010,&nbsp;elaborar cartas de zonas inund&aacute;veis para &aacute;reas de risco e&nbsp;cartas de riscos de inunda&ccedil;&otilde;es e aprovar planos de gest&atilde;o&nbsp;dos riscos de inunda&ccedil;&otilde;es, nos termos previstos no presente&nbsp;decreto -lei.</p>
<p style="text-align: justify; ">2 &mdash; As ARH, ouvida a CNGRI, para efeitos do disposto&nbsp;nos artigos 7.&ordm; e 8.&ordm;, podem decidir utilizar cartas de&nbsp;zonas inund&aacute;veis para &aacute;reas de risco e as cartas de riscos&nbsp;de inunda&ccedil;&otilde;es finalizadas antes de 22 de Dezembro de&nbsp;2010, se essas cartas fornecerem um n&iacute;vel de informa&ccedil;&atilde;o&nbsp;equivalente aos requisitos estabelecidos nesses artigos.</p>
<p style="text-align: justify; ">3 &mdash; Para efeitos do disposto no n&uacute;mero anterior, podem&nbsp;ser utilizadas:<br />
a) As zonas inund&aacute;veis ou amea&ccedil;adas pelas cheias,&nbsp;definidas nos termos do artigo 40.&ordm; da Lei da &Aacute;gua;<br />
b) As zonas adjacentes, delimitadas nos termos do&nbsp;Decreto -Lei n.&ordm; 468/71, de 5 de Novembro, e da Lei da&nbsp;Titularidade dos Recursos H&iacute;dricos;<br />
c) As zonas delimitadas nas cartas de zonas inund&aacute;veis,&nbsp;elaboradas ao abrigo do Decreto -Lei n.&ordm; 364/98, de 21 de&nbsp;Novembro;<br />
d) As zonas amea&ccedil;adas pelas cheias e pelo mar inclu&iacute;das&nbsp;na Reserva Ecol&oacute;gica Nacional;<br />
e) As zonas amea&ccedil;adas pelas cheias e pelo mar exclu&iacute;das&nbsp;da Reserva Ecol&oacute;gica Nacional no &acirc;mbito do procedimento&nbsp;de delimita&ccedil;&atilde;o realizado ao abrigo do Decreto -Lei&nbsp;n.&ordm; 166/2008, de 22 de Agosto.</p>
<p style="text-align: justify; ">4 &mdash; As ARH, ouvida a CNGRI, para efeitos do disposto&nbsp;no artigo 9.&ordm;, podem tamb&eacute;m decidir utilizar os planos de&nbsp;gest&atilde;o dos riscos de inunda&ccedil;&otilde;es finalizados antes de 22 de&nbsp;Dezembro de 2010, desde que o conte&uacute;do desses planos&nbsp;seja equivalente aos requisitos estabelecidos nesse artigo.</p>
<p style="text-align: justify; ">5 &mdash; O disposto nos n&uacute;meros anteriores n&atilde;o prejudica&nbsp;a reavalia&ccedil;&atilde;o prevista no artigo anterior.</p>', '2010-10-29 09:56:14.090000', null, false, false, true, 22, 260, 11, 'DL_115_2010.pdf', null, null);
