INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (537, 'Estabelece  as bases e o quadro institucional para a gestão sustentável das águas - Republicada no DL 130/2012 de 22 de Junho', '58', '2005', '2005-12-29', 'ambiente água lei gestão sustentável águas', 'Aprova a Lei da Água, transpondo para a ordem jurídica nacional a Directiva n.º 2000/60/CE, do Parlamento Europeu e do Conselho de 23 de Outubro, e estabelecendo as bases e o quadro institucional para a gestão sustentável das águas', '<p><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong></p>
<p>1&mdash; A presente lei tem por &acirc;mbito de aplica&ccedil;&atilde;o a totalidade dos recursos h&iacute;dricos referidos no n.&ordm; 1 do artigo anterior qualquer que seja o seu regime jur&iacute;dico, abrangendo, al&eacute;m das &aacute;guas, os respectivos leitos e margens, bem como as zonas adjacentes, zonas de infiltra&ccedil;&atilde;o m&aacute;xima e zonas protegidas.</p>
<p>2&mdash; O disposto na presente lei n&atilde;o prejudica a aplica&ccedil;&atilde;o dos regimes especiais relativos, nomeadamente, &agrave;s &aacute;guas para consumo humano, aos recursos hidrominerais geot&eacute;cnicos e &aacute;guas de nascente, &agrave;s &aacute;guas destinadas a fins terap&ecirc;uticos e &agrave;s &aacute;guas que alimentem piscinas e outros recintos com divers&otilde;es aqu&aacute;ticas.</p>
<p><strong>ENTIDADES REGULADORAS: </strong></p>
<p>- ARH - Administra&ccedil;&otilde;es de Regi&otilde;es Hidrigr&aacute;ficas;</p>
<p>- INAG - Instituto da &Aacute;gua.</p>
<p><strong>DIRECTIVAS EUROPEIAS RELACIONADAS: </strong></p>
<p>- Directiva 2000/60/CE, do Parlamento Europeu e do Conselho, de 23 de Outubro</p>
<p><strong>DIPLOMAS ALTERADOS: </strong></p>
