INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1580, 'Critérios e normas técnicas para a elaboração e operacionalização de planos de emergência de protecção civil', '30', '2015', '2015-05-07', 'Protecção , civil , nacional , planos , emergência , PME , PEE', 'Aprova a diretiva relativa aos critérios e normas técnicas para a elaboração e operacionalização de planos de emergência de proteção civil', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p><span style="color: #003366"><span style="color: #000000">Aplic&aacute;vel a todas as entidades p&uacute;blicas ou privadas com compet&ecirc;ncias no dom&iacute;nio da protec&ccedil;&atilde;o civil.</span></span></p>
