INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (880, 'Regulamento das emissões sonoras para o ambiente de equipamento para utilização no exterior', '76', '2002', '2002-03-26', 'emissões sonoras ambiente equipamento', 'Aprova o regulamento das emissões sonoras para o ambiente do equipamento para utilização no exterior, transpondo para o ordenamento jurídico interno a Directiva n.º 2000/14/CEE, do Parlamento Europeu e do Conselho, de 8 de Maio', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p style="text-align: justify">1 &mdash; As disposi&ccedil;&otilde;es do presente Regulamento aplicam-se ao equipamento para utiliza&ccedil;&atilde;o no exterior enumerado nos artigos 12.&ordm; e 13.&ordm; do Regulamento das Emiss&otilde;es Sonoras de Equipamento para Utiliza&ccedil;&atilde;o no Exterior e, descrito no respectivo, do qual faz parte integrante.<br />
2 &mdash; O presente Regulamento abrange exclusivamente equipamentos colocados no mercado nacional ou em utiliza&ccedil;&atilde;o no territ&oacute;rio nacional como unidades integrais adequadas ao fim pretendido, e n&atilde;o se aplica aos acess&oacute;rios sem transmiss&atilde;o colocados no mercado ou em utiliza&ccedil;&atilde;o separadamente, salvo aos martelos-demolidores, martelos-perfuradores manuais e martelos hidr&aacute;ulicos.<br />
3 &mdash; Excluem-se do &acirc;mbito do presente Regulamento:<br />
a) Todo o equipamento originalmente destinado ao transporte de mercadorias ou de pessoas por via rodovi&aacute;ria, ferrovi&aacute;ria, a&eacute;rea, fluvial ou mar&iacute;tima;<br />
b) O equipamento especialmente projectado e constru&iacute;do para fins militares ou de pol&iacute;cia e para servi&ccedil;os de emerg&ecirc;ncia.</p>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong></span></p>
<p>- Direc&ccedil;&atilde;o-Geral da Ind&uacute;stria (DGI);</p>
<p>- Direc&ccedil;&atilde;o-Geral do Com&eacute;rcio e da Concorr&ecirc;ncia (DGCC);</p>
