INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (72, 'Regime jurídico das armas e suas munições', '5', '2006', '2006-02-23', 'Security  protecção regime jurídico armas munições', 'Estabelece o regime jurídico relativo ao fabrico, montagem, reparação, importação, exportação, transferência, armazenamento, circulação, comércio, aquisição, cedência, detenção, manifesto, guarda, segurança, uso e porte de armas, seus componentes e munições, bem como o enquadramento legal das operações especiais de prevenção criminal.', '<p><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></p>
<p><strong> </strong> A presente lei estabelece o regime jur&iacute;dico relativo ao fabrico, montagem, repara&ccedil;&atilde;o, importa&ccedil;&atilde;o, exporta&ccedil;&atilde;o, transfer&ecirc;ncia, armazenamento, circula&ccedil;&atilde;o, com&eacute;rcio, aquisi&ccedil;&atilde;o, ced&ecirc;ncia, deten&ccedil;&atilde;o, manifesto, guarda, seguran&ccedil;a, uso e porte de armas, seus componentes e muni&ccedil;&otilde;es, bem como o enquadramento legal das opera&ccedil;&otilde;es especiais de preven&ccedil;&atilde;o criminal. Ficam exclu&iacute;das do &acirc;mbito de aplica&ccedil;&atilde;o da presente lei as actividades relativas a armas e muni&ccedil;&otilde;es destinadas &agrave;s For&ccedil;as Armadas, &agrave;s for&ccedil;as e servi&ccedil;os de seguran&ccedil;a, bem como a outros servi&ccedil;os p&uacute;blicos cuja lei expressamente as exclua, bem como aquelas que se destinem exclusivamente a fins militares. Ficam ainda exclu&iacute;das do &acirc;mbito de aplica&ccedil;&atilde;o da presente lei as actividades referidas no n.o 1 relativas a armas de fogo cuja data de fabrico seja anterior a 31 de Dezembro de 1890, bem como aquelas que utilizem muni&ccedil;&otilde;es obsoletas, constantes do anexo a este diploma e que dele faz parte integrante, e que pelo seu interesse hist&oacute;rico, t&eacute;cnico e art&iacute;stico possam ser preservadas e conservadas em colec&ccedil;&otilde;es p&uacute;blicas ou privadas.</p>
<p><strong>ENTIDADES REGULADORAS:</strong></p>
<p>Direc&ccedil;&atilde;o Nacional da Pol&iacute;cia de Seguran&ccedil;a P&uacute;blica</p>
