INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (802, 'Institui o sistema nacional de controlo de tráfego marítimo (SNCTM)', '263', '2009', '2009-09-28', 'security sistema nacional controlo tráfego marítimo SNCTM', 'Institui o sistema nacional de controlo de tráfego marítimo (SNCTM), criando um quadro geral de intervenção dos órgãos e serviços públicos responsáveis pelo controlo de tráfego marítimo nas zonas marítimas sob soberania ou jurisdição nacional, e procede à 1.ª alteração do Decreto-Lei n.º 43/2002, de 2 de Março, à 3.ª alteração do Decreto-Lei n.º 180/2004, de 27 de Julho, e à 1.ª alteração do Decreto-Lei n.º 198/2006, de 19 de Outubro', '<p style="text-align: justify"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></p>
<ol>
    <li>
    <p>Encontram-se sujeitos ao VTS costeiro do continente os navios com arquea&ccedil;&atilde;o bruta igual ou superior a 300 GRT.</p>
    </li>
    <li>
    <p>Encontram-se ainda sujeito(a)s ao VTS costeiro do continente, independentemente da sua arquea&ccedil;&atilde;o:</p>
    </li>
</ol>
<p style="margin-left: 80px; ">a) Os navios que transportem mercadorias perigosas ou poluentes, na acep&ccedil;&atilde;o do disposto no Decreto-Lei n.&ordm; 180/2004, de 27 de Julho;</p>
<p style="margin-left: 80px; ">b) Os navios que efectuem o transporte de passageiros;</p>
<p style="margin-left: 80px; ">c) Os navios de pesca de comprimento igual ou superior a 24 m;</p>
<p style="margin-left: 80px; ">d) As embarca&ccedil;&otilde;es de recreio de comprimento igual ou superior a 24 m;</p>
<p style="margin-left: 80px; ">e) Os navios que participem em opera&ccedil;&otilde;es de reboque, nas quais o conjunto rebocador e reboque seja superior a 100 m de comprimento.</p>
<p style="text-align: justify"><strong>ENTIDADES REGULADORAS:</strong></p>
<p style="text-align: justify">Compete ao IPTM, I. P., &agrave; ANCTM, atrav&eacute;s do CCTMC, e &agrave; AMN, atrav&eacute;s dos meios navais dispon&iacute;veis</p>
<p style="text-align: justify"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS: </strong>n/a</p>
