INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1138, 'Exposição a agentes químicos no trabalho', '24', '2012', '2012-02-06', 'safety substâncias perigosas protecção segurança saúde trabalhadores riscos ligados exposição agentes químicos trabalho valores limite', 'O presente diploma consolida as prescrições mínimas em matéria de protecção dos trabalhadores contra os riscos para a segurança e a saúde devido à exposição a agentes químicos no trabalho e transpõe para a ordem interna a Directiva n.º 2009/161/UE, da Comissão, de 17 de Dezembro de 2009, que estabelece uma terceira lista de valores limite de exposição profissional indicativos para a aplicação da Directiva n.º 98/24/CE, do Conselho, de 7 de Abril de 1998, e altera a Directiva n.º 2000/39/CE, de 8 de Junho de 2000.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p style="text-align: justify"><span style="color: rgb(0,0,0)">Aplic&aacute;vel a todas as actividades dos sectores privado, cooperativo e social, da administra&ccedil;&atilde;o p&uacute;blica central, regional e local, dos institutos p&uacute;blicos e das demais pessoas colectivas de direito p&uacute;blico, bem como a trabalhadores por conta pr&oacute;pria</span>.</p>
<p style="text-align: justify"><span style="color: rgb(0,0,0)">Ao transporte de mercadorias perigosas, sem preju&iacute;zo de disposi&ccedil;&otilde;es previstas em legisla&ccedil;&atilde;o especial.</span></p>
<p style="text-align: justify"><span style="color: rgb(0,0,0)">O presente diploma n&atilde;o prejudica a aplica&ccedil;&atilde;o:</span></p>
<ul>
