INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (389, 'Protecção contra os riscos de exposição ao amianto', '266', '2007', '2007-07-24', 'safety substâncias perigosas amianto', 'Transpõe para a ordem jurídica interna a Directiva n.º 2003/18/CE, do Parlamento Europeu e do Conselho, de 27 de Março, que altera a Directiva n.º 83/477/CEE, do Conselho, de 19 de Setembro, relativa à protecção sanitária dos trabalhadores contra os riscos de exposição ao amianto durante o trabalho', '<p style="text-align: justify"><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></span></p>
<p style="text-align: justify">O presente decreto-lei &eacute; aplic&aacute;vel em todas as actividades em que os trabalhadores est&atilde;o ou podem estar expostos a poeiras do amianto ou de materiais que contenham amianto, nomeadamente: a) Demoli&ccedil;&atilde;o de constru&ccedil;&otilde;es em que existe amianto ou materiais que contenham amianto; b) Desmontagem de m&aacute;quinas ou ferramentas em que existe amianto ou materiais que contenham amianto; c) Remo&ccedil;&atilde;o do amianto ou de materiais que contenham amianto de instala&ccedil;&otilde;es, estruturas, edif&iacute;cios ou equipamentos, bem como aeronaves, material circulante ferrovi&aacute;rio, navios ou ve&iacute;culos; d) Manuten&ccedil;&atilde;o e repara&ccedil;&atilde;o de materiais que contenham amianto existentes em instala&ccedil;&otilde;es, estruturas, edif&iacute;cios ou equipamentos, bem como em aeronaves, carruagens de comboios, navios ou ve&iacute;culos; e) Transporte, tratamento e elimina&ccedil;&atilde;o de res&iacute;duos que contenham amianto; f) Aterros autorizados para res&iacute;duos de amianto. O presente decreto-lei &eacute; aplic&aacute;vel nos sectores privado, cooperativo e social, na administra&ccedil;&atilde;o p&uacute;blica central, regional e local, institutos p&uacute;blicos e demais pessoas colectivas de direito p&uacute;blico, bem como a trabalhadores independentes que desenvolvam actividades referidas no n&uacute;mero anterior.</p>
