INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (68, 'Homologação dos Tractores Agrícolas e Florestais de Rodas', '305', '2001', '2001-12-03', 'SHT máquinas equipamentos tractores agrícolas', 'É aditado ao Regulamento da Homologação dos Tractores Agrícolas e Florestais de Rodas um capítulo XI e seus anexos', '<p style="text-align: justify; "><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></p>
<p style="text-align: justify; ">O presente Regulamento aplica-se exclusivamente aos tractores segundo defini&ccedil;&atilde;o (a), montados sobre pneus, tendo dois eixos e uma velocidade m&aacute;xima, por constru&ccedil;&atilde;o, compreendida entre 6 km/h e 40 km/h. O disposto no presente Regulamento n&atilde;o se aplica &agrave; homologa&ccedil;&atilde;o individual de tractores nem a tractores anteriormente matriculados.</p>
<p style="margin-left: 40px; text-align: justify; ">a) Tractores: Tractor agr&iacute;cola ou florestal: ve&iacute;culo a motor, com rodas, tendo pelo menos dois eixos, cuja fun&ccedil;&atilde;o N.o 263 &mdash; 14 de Novembro de 2000 DI&Aacute;RIO DA REP&Uacute;BLICA &mdash; I S&Eacute;RIE-A 6441 principal reside na pot&ecirc;ncia de trac&ccedil;&atilde;o especialmente concebida para atrelar, empurrar, carregar ou accionar certas ferramentas, m&aacute;quinas ou reboques destinados &agrave; utiliza&ccedil;&atilde;o agr&iacute;cola ou florestal, podendo estar equipado para transportar uma carga e passageiros.</p>
<p style="text-align: justify; "><strong>ENTIDADES REGULADORAS:</strong></p>
<p>Direc&ccedil;&atilde;o Geral de Via&ccedil;&atilde;o</p>
