INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (275, 'Tabela nacional de incapacidades, tabela de avaliação de incapacidades permanentes e tabela indicativa para a avaliação da incapacidade', '352', '2007', '2007-10-23', 'safety SHT segurança higiene trabalho acidentes doenças profissionais', 'Aprova a nova Tabela Nacional de Incapacidades por Acidentes de Trabalho e Doenças Profissionais e a Tabela Nacional para Avaliação de Incapacidades Permanentes em Direito Civil, constantes respectivamente dos Anexos I e II ao presente Decreto-lei e que dele fazem parte integrante, revogando o Decreto-Lei n.º 341/93, de 30 de Setembro, e aprova a Tabela Indicativa para a Avaliação da Incapacidade em Direito Civil', '<p style="text-align: justify; "><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></p>
<p style="text-align: justify; ">A incapacidade do sinistrado ou doente no &acirc;mbito do direito do trabalho e a incapacidade permanente do lesado no dom&iacute;nio do direito civil s&atilde;o calculadas respectivamente em conformidade com a Tabela Nacional de Incapacidades por Acidentes de Trabalho e Doen&ccedil;as Profissionais e a Tabela Nacional para Avalia&ccedil;&atilde;o de Incapacidades Permanentes em Direito Civil, observando-se as instru&ccedil;&otilde;es gerais e espec&iacute;ficas nelas constantes. Na avalia&ccedil;&atilde;o do sinistrado ou doente &eacute; tido em conta o disposto no artigo 41.&ordm; do Decreto-Lei n.&ordm; 143/99, de 30 de Abril, no artigo 78.&ordm; do Decreto-Lei n.&ordm; 248/99, de 2 de Julho e no artigo 38.&ordm; do Decreto-Lei n.&ordm; 503/99, de 20 de Novembro. A incapacidade permanente do lesado para efeitos de repara&ccedil;&atilde;o civil do dano &eacute; calculada por m&eacute;dicos especialistas em medicina legal ou por especialistas noutras &aacute;reas com compet&ecirc;ncia espec&iacute;fica no &acirc;mbito da avalia&ccedil;&atilde;o m&eacute;dico-legal do dano corporal no dom&iacute;nio do direito civil e das respectivas regras, os quais ficam vinculados &agrave; exposi&ccedil;&atilde;o dos motivos justificativos dos desvios em rela&ccedil;&atilde;o &agrave;s pontua&ccedil;&otilde;es previstas na Tabela Nacional para Avalia&ccedil;&atilde;o de Incapacidades Permanentes em Direito Civil.</p>
<p><strong>ENTIDADES REGULADORAS:</strong></p>
<p>Autoridade para as Condi&ccedil;&otilde;es de Trabalho (ACT)</p>
<p><strong>DIRECTIVAS RELACIONADAS:</strong></p>
<p style="text-align: justify; ">Tabela Europeia intitulada &quot;Guide bar&eacute;me europe&eacute;n d''evaluation d&ecirc;s atteintes &agrave; l''int&eacute;grit&eacute; physique e psychique&quot;; Classifica&ccedil;&atilde;o Internacional da Funcionalidade da responsbilidade da Organiza&ccedil;&atilde;o Mundial de Sa&uacute;de; Plano de Ac&ccedil;&atilde;o para a Integra&ccedil;&atilde;o das Pessoas com Defici&ecirc;ncias ou Incapacidade</p>
<p><strong>DIPLOMAS ALTERADOS: </strong>n/a</p>
<p><strong>DIPLOMAS REVOGADOS:</strong></p>
<ul>
