INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (280, 'Acidentes em serviço e doenças profissionais ocorridos ao serviço da Administração Pública', '503', '1999', '1999-11-20', 'safety SHT segurança higiene trabalho acidentes serviço doenças profissionais regime jurídico Administração Pública', 'Aprova o novo regime jurídico dos acidentes em serviço e doenças profissionais ocorridos ao serviço da Administração Pública', '<p style="text-align: justify; "><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></p>
<p style="text-align: justify; ">O disposto no presente diploma &eacute; aplic&aacute;vel aos funcion&aacute;rios, agentes e outros trabalhadores que sejam subscritores da Caixa Geral de Aposenta&ccedil;&otilde;es e exer&ccedil;am fun&ccedil;&otilde;es na administra&ccedil;&atilde;o central, local e regional, incluindo os institutos p&uacute;blicos nas modalidades de servi&ccedil;os personalizados e de fundos p&uacute;blicos e ainda nos servi&ccedil;os e organismos que estejam na depend&ecirc;ncia org&acirc;nica e funcional da Presid&ecirc;ncia da Rep&uacute;blica e da Assembleia da Rep&uacute;blica. Ao pessoal dos servi&ccedil;os referidos anteriormente, vinculado por contrato individual de trabalho, com ou sem termo, e enquadrado no regime geral de seguran&ccedil;a social, aplica-se o regime jur&iacute;dico dos acidentes de trabalho e das doen&ccedil;as profissionais constante da Lei n.&ordm; 100/1997, de 13 de Setembro. O pessoal contratado em regime de presta&ccedil;&atilde;o de servi&ccedil;os fica sujeito ao disposto no artigo 3.&ordm; da Lei n.&ordm; 100/1997, de 13 de Setembro, devendo efectuar um seguro que garanta as presta&ccedil;&otilde;es nela previstas.</p>
<p style="text-align: justify; "><strong>ENTIDADES REGULADORAS:</strong></p>
<p style="text-align: justify; ">Autoridade para as Condi&ccedil;&otilde;es de Trabalho (ACT)</p>
<p style="text-align: justify; "><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></p>
