INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1196, 'Criação do Fundo Sanitário e de Segurança Alimentar  Mais', '119', '2012', '2012-06-15', 'Segurança Alimentar', 'O presente diploma cria, no âmbito do Ministério da Agricultura, do Mar, do Ambiente e do ordenamento do Território, o Fundo Sanitário e de Segurança Alimentar Mais, doravante designado por Fundo, bem como a taxa de segurança alimentar mais.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p><span style="color: #003366">Estabelecimentos de com&eacute;rcio alimentar de produtos de origem animal e vegetal, frescos ou congelados, transformados ou crus, a granel ou pr&eacute; &ndash;embalados. </span></p>
<p>&nbsp;Est&atilde;o isentos os estabelecimentos com uma &aacute;rea de venda inferior a 2000 m<sup>2</sup> ou pertencentes a microempresas desde que:</p>
<div><i>a</i>) N&atilde;o perten&ccedil;am a uma empresa que utilize uma ou mais ins&iacute;gnias e que disponha, a n&iacute;vel nacional, de uma &aacute;rea de venda acumulada igual ou superior a 6000 m2;</div>
<div><i>b</i>) N&atilde;o estejam integrados num grupo que disponha, a n&iacute;vel nacional, de uma &aacute;rea de venda acumulada igual ou superior a 6000 m2.</div>
<div>Para efeitos do presente diploma, entende -se por &laquo;estabelecimento de com&eacute;rcio alimentar&raquo; o local no qual se exerce uma atividade de com&eacute;rcio alimentar a retalho, incluindo os estabelecimentos de com&eacute;rcio misto, tal como definidos na al&iacute;nea <i>l</i>) do artigo 4.&ordm; do Decreto &ndash;Lei n.&ordm; 21/2009, de 19 de janeiro.</div>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong></span></p>
<ul>
    <li><span style="color: rgb(0,0,0)">Dire&ccedil;&atilde;o-Geral de Alimenta&ccedil;&atilde;o e Veterin&aacute;ria (DGAV)</span></li>
    <li><span style="color: rgb(0,0,0)">Fiscaliza&ccedil;&atilde;o: Autoridade de Seguran&ccedil;a Alimentar e Econ&oacute;mica (ASAE)</span></li>
</ul>
