INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1191, 'DLR 24/2012/A - Aprova as normas que regulamentam a gestão de fluxos  específicos de resíduos', '24/A', '2012', '2012-06-01', 'Gestão Resíduos Açores', 'O presente diploma estabelece o regime jurídico a que fica sujeita a gestão dos seguintes fluxos específicos de resíduos:
a) Pneus e pneus usados;
b) Óleos minerais novos e usados;
c) Veículos e veículos em fim de vida e seus componentes e materiais;
d) Equipamentos elétricos e eletrónicos e resíduos de equipamentos elétricos e eletrónicos;
e) Pilhas, acumuladores e baterias, e respetivos resíduos;
f) Óleos alimentares, novos e usados, produzidos pelos sectores industrial, da hotelaria e restauração e doméstico.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<ul>
    <li>
    <p><span style="color: rgb(0,0,0)">O presente diploma aplica-se &agrave; gest&atilde;o de fluxos de res&iacute;duos realizada na Regi&atilde;o Aut&oacute;noma dos A&ccedil;ores, qualquer que seja a origem ou proveni&ecirc;ncia dos res&iacute;duos.</span></p>
    </li>
    <li>
    <p><span style="color: rgb(0,0,0)">O presente diploma &eacute; aplic&aacute;vel a todos os produtos a que se refere o n.&ordm; 1 do artigo anterior, bem como aos respetivos res&iacute;duos.</span></p>
    </li>
</ul>
<p><span style="color: #003366"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></span></p>
<ul>
    <li>Diretiva n.&ordm; 1987/101/CEE, do Conselho, de 22 de dezembro de 1986, que altera a Diretiva n.&ordm; 75/439/CEE, do Conselho, de 16 de junho;</li>
    <li>Diretiva n.&ordm; 2000/53/CE, do Parlamento Europeu e do Conselho, de 18 de setembro, relativa aos ve&iacute;culos em fim de vida, alterada pela Diretiva n.&ordm; 2011/37/EU, da Comiss&atilde;o, de 30 de mar&ccedil;o, que altera o anexo II da Diretiva n.&ordm; 2000/53/CE, do Parlamento Europeu e do Conselho, de 18 de setembro;</li>
    <li>Diretiva n.&ordm; 2011/65/UE, do Parlamento Europeu e do Conselho, de 8 de junho;</li>
    <li>Diretiva n.&ordm; 2002/96/CE, do Parlamento Europeu e do Conselho, de 27 de janeiro de 2003, alterada pela Diretiva n.&ordm; 2003/108/CE, do Parlamento Europeu e do Conselho, de 8 de dezembro;</li>
    <li>Diretiva n.&ordm; 2008/103/CE, do Parlamento Europeu e do Conselho, de 19 de novembro, que altera a Diretiva n.&ordm; 2006/66/CE, do Parlamento Europeu e do Conselho, de 6 de setembro;</li>
    <li>Diretiva n.&ordm; 2008/112/CE, do Parlamento Europeu e do Conselho, de 16 de dezembro, na parte em que esta altera as Diretivas n.os 2002/96/CE, do Parlamento Europeu e do Conselho, de 27 de janeiro de 2003, e 2000/53/CE, do Parlamento Europeu e do Conselho, de 18 de setembro, a <br />
    fim de a adaptar ao Regulamento (CE) n.&ordm; 1272/2008, do Parlamento Europeu e do Conselho, de 16 de dezembro.</li>
</ul>
<p><span style="color: #003366"><strong>DIPLOMAS ALTERADOS:</strong> </span>n/a</p>
