INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1063, 'Estabelece o regime jurídico aplicável aos equipamentos sob pressão transportáveis', '57', '2011', '2011-04-27', 'equipamentos sob pressão transportáveis', 'Estabelece o regime jurídico aplicável aos equipamentos sob pressão transportáveis e revoga o Decreto-Lei n.º 41/2002, de 28 de Fevereiro, transpondo a Directiva n.º 2010/35/UE, do Parlamento Europeu e do Conselho, de 16 de Junho', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p style="text-align: justify">O presente decreto -lei aplica -se:</p>
<ul>
    <li>Aos equipamentos sob press&atilde;o transport&aacute;veis novos definidos no artigo 2.&ordm; que n&atilde;o ostentem a marca&ccedil;&atilde;o de conformidade prevista no Decreto -Lei n.&ordm; 41/2002, de 28 de Fevereiro, para efeitos da sua disponibiliza&ccedil;&atilde;o no mercado;</li>
    <li>Aos equipamentos sob press&atilde;o transport&aacute;veis definidos no artigo 2.&ordm; que ostentem a marca&ccedil;&atilde;o de conformidade prevista no presente decreto -lei ou nas Directivas n.os 84/525/CEE, 84/526/CEE e 84/527/CEE, de 17 de Setembro, ou 1999/36/CE, de 29 de Abril, todas do Conselho, para efeitos das suas inspec&ccedil;&otilde;es peri&oacute;dicas, inspec&ccedil;&otilde;es intercalares, verifica&ccedil;&otilde;es excepcionais e utiliza&ccedil;&atilde;o;</li>
    <li>Aos equipamentos sob press&atilde;o transport&aacute;veis definidos no artigo 2.&ordm; que n&atilde;o ostentem a marca&ccedil;&atilde;o de conformidade prevista na Directiva n.&ordm; 1999/36/CE, do Conselho, de 29 de Abril, para efeitos da reavalia&ccedil;&atilde;o da sua conformidade.</li>
</ul>
<p style="text-align: justify">Consultar a defini&ccedil;&atilde;o de equipamentos sob press&atilde;o transport&aacute;veis no Artigo 2&ordm; deste diploma.</p>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong><font color="#000000">&nbsp;</font></span></p>
<p><span style="color: #003366"><font color="#000000">- Instituto da Mobilidade e dos Transportes Terrestres, I. P. (IMTT, I. P.)&nbsp;</font></span></p>
<p><span style="color: #003366"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></span></p>
<p>- Directiva n.&ordm; 2010/35/UE, do Parlamento Europeu e do Conselho, de 16 de Junho</p>
<p><span style="color: #003366"><strong>DIPLOMAS ALTERADOS:</strong> </span>n/a</p>
<p><span style="color: #003366"><strong>DIPLOMAS REVOGADOS:</strong></span><span style="color: #333399"><strong> </strong></span></p>
<p><span style="color: #333399"><strong>- </strong></span>Decreto-Lei n.&ordm; 41/2002, de 28 de Fevereiro</p>
<p><span style="color: #003366"><strong>DIPLOMAS REPUBLICADOS:</strong></span><strong> </strong>n/a</p>
<p><span style="color: #003366"><strong>ALTERA&Ccedil;&Otilde;ES A ESTE DIPLOMA:</strong> </span>n/a</p>
<p><span style="color: #003366"><strong>REVOGA&Ccedil;&Otilde;ES DE ARTIGOS DESTE DIPLOMA: </strong></span>n/a</p>', '<p style="text-align: justify">- A al&iacute;nea d) do n.&ordm; 2 do artigo 19.&ordm; &eacute; aplicada com efeitos a partir de 1 de Janeiro de 2012.</p>
<p style="text-align: justify">- As disposi&ccedil;&otilde;es do presente decreto -lei aplicam-se aos recipientes sob press&atilde;o, suas v&aacute;lvulas e outros acess&oacute;rios utilizados para o transporte das mercadorias com os n&uacute;meros ONU 1745, ONU 1746 e ONU 2495 a partir de 1 de Julho de 2013.</p>
<p style="text-align: justify">- O artigo 36.&ordm; (reconhecimento de equival&ecirc;ncias) aplica -se a partir de 1 de Julho de 2011.</p>', '2011-05-10 10:05:33.057000', null, false, false, true, 22, 260, 11, 'DL_57_2011.pdf', null, '<p>Art. 9.&ordm;, 10.&ordm;, 12.&ordm;, 13.&ordm;</p>');
