INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1210, 'Regulamenta a taxa de segurança alimentar', '215', '2012', '2012-07-17', 'Taxa Segurança Alimentar', 'Regulamenta a taxa de segurança alimentar.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p style="text-align: justify">A taxa &eacute; devida pelos titulares de estabelecimentos de com&eacute;rcio alimentar de produtos de origem animal e vegetal, frescos ou congelados, transformados ou crus, a granel ou pr&eacute; -embalados, de acordo com a &aacute;rea de venda do estabelecimento.</p>
<p style="text-align: justify">Para efeitos do n&uacute;mero anterior, entende-se por:<br />
a) &laquo;Estabelecimento de com&eacute;rcio alimentar&raquo; o local no qual se exerce uma atividade de com&eacute;rcio alimentar a retalho, incluindo os estabelecimentos de com&eacute;rcio misto, tal como definidos na al&iacute;nea l) do artigo 4.&ordm; do Decreto -Lei n.&ordm; 21/2009, de 19 de janeiro;<br />
b) &laquo;&Aacute;rea de venda do estabelecimento&raquo; toda a &aacute;rea destinada a venda, onde os compradores t&ecirc;m acesso ou os produtos se encontram expostos ou s&atilde;o preparados para entrega imediata.</p>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong></span></p>
<ul>
    <li><span style="color: rgb(0,0,0)">Dire&ccedil;&atilde;o-Geral de Alimenta&ccedil;&atilde;o e Veterin&aacute;ria (DGAV)</span></li>
    <li><span style="color: rgb(0,0,0)">Fiscaliza&ccedil;&atilde;o: Autoridade de Seguran&ccedil;a Alimentar e Econ&oacute;mica (ASAE)</span></li>
</ul>
<p><span style="color: #003366"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></span> n/a</p>
<p><span style="color: #003366"><strong>DIPLOMAS ALTERADOS:</strong> </span>n/a</p>
<p><span style="color: #003366"><strong>DIPLOMAS REVOGADOS:</strong></span><span style="color: #333399"><strong> </strong></span>n/a</p>
<p><span style="color: #003366"><strong>DIPLOMAS REPUBLICADOS:</strong></span><strong> </strong>n/a</p>
<p><span style="color: #003366"><strong>ALTERA&Ccedil;&Otilde;ES A ESTE DIPLOMA:</strong> </span>n/a</p>
<p><span style="color: #003366"><strong>REVOGA&Ccedil;&Otilde;ES DE ARTIGOS DESTE DIPLOMA: </strong></span>n/a</p>', '<p>1 &mdash; Para efeitos do disposto no n.&ordm; 1 do artigo 17.&ordm; do Decreto -Lei n.&ordm; 119/2012, de 15 de junho, o valor da taxa para o ano de 2012 &eacute; de &euro; 4,08 por metro quadrado de &aacute;rea de venda do estabelecimento comercial.<br />
2 &mdash; O montante da taxa referente ao ano de 2012 &eacute; notificado aos sujeitos passivos, para a respetiva caixa postal eletr&oacute;nica ou por via postal, devendo o pagamento ser efetuado no prazo de 60 dias a contar dessa notifica&ccedil;&atilde;o.<br />
3 &mdash; Para efeitos do presente artigo, os sujeitos passivos devem comunicar &agrave; DGAV, no prazo de 30 dias a contar da data de entrada em vigor da presente portaria, os elementos previstos no n.&ordm; 2 do artigo 5.&ordm; relativos aos respetivos estabelecimentos comerciais, sem preju&iacute;zo de liquida&ccedil;&atilde;o com base na informa&ccedil;&atilde;o relevante dispon&iacute;vel nos termos do n.&ordm; 2 do artigo anterior.<br />
4 &mdash; Durante o ano de 2012, o pagamento da taxa pode ser efetuado atrav&eacute;s de multibanco ou cheque.</p>', '2012-08-29 14:28:01.789000', null, false, false, true, 22, 294, 11, 'PORT_215_2012_17072012.PDF', null, null);
INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (788, 'Colocação no mercado de produtos fitofarmacêuticos - Altera o DL 94/1998 de 15 de Abril', '87', '2009', '2009-04-03', 'Safety SHT segurança substâncias perigosas fitofarmacêuticos', '1 — O presente decreto-lei transpõe para a ordem jurídica interna as Directivas 2007/76/CE, da Comissão, de 20 de Dezembro, 2008/40/CE, da Comissão, de 28 de Março,  2008/41/CE, da Comissão, de 31 de Março, 2008/66/CE, da Comissão, de 30 de Junho, 2008/69/CE, da Comissão, de 1 de Julho, 2008/70/CE, da Comissão, de 11 de Julho, e  2008/91/CE, da Comissão, de 29 de Setembro, que alteram a Directiva n.º 91/414/CEE, do Conselho, de 15 de Julho, incluindo na lista positiva comunitária (LPC) as substâncias activas amidossulfurão, bifenox, clofentezina, clomazona, cloridazão, dicamba, difenoconazol, diflubenzurão, diflufenicão, diurão, fenepropidina, fenoxaprope -P, fludioxonil, imazaquina, lenacil, nicossulfurão, oxadiazão, piclorame, piriproxifena, prossulfocarbe, quinoclamina e tritossulfurão.
