INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1352, 'DLR 4/2013/A - Regime jurídico aplicável ao transporte rodoviário de mercadorias por conta de outrem efetuado na Região Autónoma dos Açores por meio de veículos com peso bruto igual ou superior a 2500 kg - Republicação do DLR 7/2010/A', '4/A', '2013', '2013-05-24', 'Transporte , rodoviário , mercadorias', 'Segunda alteração e republicação ao Decreto Legislativo Regional n.º 7/2010/A, de 5 de março, que estabelece o regime jurídico aplicável ao transporte rodoviário de mercadorias por conta de outrem efetuado na Região Autónoma dos Açores por meio de veículos com peso bruto igual ou superior a 2500 kg.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong><span style="color: #000000">&nbsp; </span><br />
</span><span style="color: #003366"><span style="color: #000000">1 &mdash; O presente diploma aplica -se ao transporte rodovi&aacute;rio de mercadorias efetuado na Regi&atilde;o Aut&oacute;noma dos A&ccedil;ores por meio de ve&iacute;culos autom&oacute;veis ou conjuntos de ve&iacute;culos de mercadorias, com peso bruto igual ou superior a 2500 kg.<br />
2 &mdash; N&atilde;o est&atilde;o abrangidos pelas normas de acesso &agrave; atividade e de acesso e organiza&ccedil;&atilde;o do mercado previstas nos cap&iacute;tulos II e III do presente diploma:<br />
a) Os transportes de produtos ou mercadorias diretamente ligados &agrave; gest&atilde;o agr&iacute;cola ou dela provenientes efetuados por meio de reboques atrelados aos respetivos tratores agr&iacute;colas;<br />
b) Os transportes de envios postais realizados no &acirc;mbito da atividade de prestador de servi&ccedil;os postais;<br />
c) A circula&ccedil;&atilde;o de ve&iacute;culos aos quais estejam ligados, de forma permanente e exclusiva, equipamentos ou m&aacute;quinas;<br />
d) Os transportes rodovi&aacute;rios de mercadorias de &acirc;mbito nacional ou internacional e os transportes de cabotagem.<br />
3 &mdash; Aos contratos de transporte de mercadorias respeitantes a presta&ccedil;&otilde;es de servi&ccedil;o a efetuar exclusivamente no territ&oacute;rio da Regi&atilde;o Aut&oacute;noma dos A&ccedil;ores &eacute; aplic&aacute;vel o regime jur&iacute;dico do contrato de transporte rodovi&aacute;rio nacional de mercadorias.<br />
</span></span></p>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong><span style="color: #000000"> </span></span></p>
<ul>
    <li><span style="color: #003366"><span style="color: #000000">Dire&ccedil;&atilde;o regional competente em mat&eacute;ria de transportes terrestres </span></span></li>
</ul>
<p><span style="color: #003366"><span style="color: #000000">Fiscaliza&ccedil;&atilde;o:<br />
