INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (851, 'DLR 7/2010/A -Transporte rodoviário de mercadorias por conta de outrem - Republicado pelo DLR 25/2012/A', '7/A', '2010', '2010-03-05', 'Transporte rodoviário mercadorias', 'Estabelece o regime jurídico aplicável ao transporte rodoviário de mercadorias por conta de outrem efectuado na Região Autónoma dos Açores por meio de veículos com peso bruto igual ou superior a 2500 kg', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></span><strong>&nbsp;</strong><br />
<span><span style="font-size: 10.5pt">1 &mdash; O presente diploma aplica -se ao transporte rodovi&aacute;rio de mercadorias efectuado na Regi&atilde;o Aut&oacute;noma dos A&ccedil;ores por meio de ve&iacute;culos autom&oacute;veis ou conjuntos de ve&iacute;culos de mercadorias, com peso bruto igual ou superior a 2500 kg.</span></span></p>
<p>&nbsp;<span style="color: #000000"><span style="font-size: 10.5pt">2 &mdash; N&atilde;o est&atilde;o abrangidos pelas normas de acesso &agrave; actividade e de acesso e organiza&ccedil;&atilde;o do mercado previstas nos cap&iacute;tulos </span><span style="font-size: 7.5pt">II </span><span style="font-size: 10.5pt">e </span><span style="font-size: 7.5pt">III </span><span style="font-size: 10.5pt">do presente diploma.</span></span></p>
<p style="text-align: justify">&nbsp;<span style="color: #003366"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></span> n/a</p>
