INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1220, 'O presente diploma procede à primeira alteração ao  Decreto-Lei n.º 108/2010, de 13 de outubro, que define  o regime jurídico das medidas necessárias para garantir o bom estado ambiental do meio marinho', '201', '2012', '2012-08-27', 'estado ambiental meio marinho', 'O presente diploma procede à primeira alteração ao Decreto -Lei n.º 108/2010, de 13 de outubro, que define o regime jurídico das medidas necessárias para garan-tir o bom estado ambiental do meio marinho até 2020 e que transpôs para a ordem jurídica interna a Diretiva 
n.º 2008/56/CE, do Parlamento Europeu e do Conselho, de 17 de junho, que estabelece um quadro de ação co-munitária no domínio da política para o meio marinho (diretiva -quadro «Estratégia marinha»).', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p>O presente Decreto-Lei aplica-se &agrave;s &aacute;guas marinhas nacionais e aos efeitos transfronteiri&ccedil;os sobre a qualidade do meio marinho na mesma regi&atilde;o ou sub-regi&otilde;es marinhas.</p>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong></span></p>
<p>Dire&ccedil;&atilde;o-Geral de Recursos Naturais, Seguran&ccedil;a e Servi&ccedil;os Mar&iacute;timos - DGRM</p>
