INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1494, 'Gestão de resíduos de equipamentos elétricos e eletrónicos (REEE)', '67', '2014', '2014-05-07', 'Ambiente resíduos REEE', 'O presente decreto-lei aprova o regime jurídico da gestão de resíduos de equipamentos elétricos e eletrónicos (REEE), estabelecendo medidas de proteção do ambiente e da saúde humana, com os objetivos de prevenir ou reduzir os impactes adversos decorrentes da produção e gestão desses resíduos, diminuir os impactes globais da utilização dos recursos, melhorar a eficiência dessa utilização, e contribuir para o desenvolvimento sustentável, transpondo para a ordem jurídica interna a Diretiva n.º 2012/19/UE, do Parlamento Europeu e do Conselho, de 4 de julho de 2012, relativa aos REEE, que reformula a Diretiva n.º 2002/96/CE, do Parlamento Europeu e do Conselho, de 27 de janeiro de 2003.', '<p><span style="color: #003366"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></span></p>
