INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (271, 'Regime jurídico da Segurança, Higiene e Saúde no Trabalho', '441', '1991', '1991-11-14', 'safety SHT segurança higiene trabalho regime jurídico', 'Estabelece o regime jurídico de enquadramento da Segurança, Higiene e Saúde no Trabalho', '<p style="text-align: justify"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></p>
<ol>
    <li style="text-align: justify">O presente diploma aplica-se:&nbsp;
    <p style="text-align: justify">a) A todos os ramos de actividade, nos sectores p&uacute;blico, privado ou cooperativo e social;</p>
    <p style="text-align: justify">b) Aos trabalhadores por conta ou ao servi&ccedil;o de outrem e aos respectivos empregados, incluindo os trabalhadores da administra&ccedil;&atilde;o p&uacute;blica central, regional e local, dos institutos p&uacute;blicos, das demais pessoas colectivas de direito p&uacute;blico e das pessoas colectivas de direito privado sem fins lucrativos e a todas estas entidades</p>
    <p style="text-align: justify">c) Ao trabalhador independente.</p>
    </li>
    <li>
    <p style="text-align: justify">Nos casos de explora&ccedil;&otilde;es agr&iacute;colas familiares, do exerc&iacute;cio da actividade da pesca em regime de &laquo;campanha&raquo; e da actividade desenvolvida por artes&atilde;os em instala&ccedil;&otilde;es pr&oacute;prias, considerar-se-&aacute; aplic&aacute;vel o regime estabelecido para o trabalhador, independente sempre que n&atilde;o se encontre prevista a adapta&ccedil;&atilde;o do regime geral &agrave;quelas situa&ccedil;&otilde;es.</p>
    </li>
    <li>
    <p style="text-align: justify">Os princ&iacute;pios definidos neste diploma ser&atilde;o adaptados ao servi&ccedil;o dom&eacute;stico, sempre que se mostrem compat&iacute;veis com o trabalho prestado, atrav&eacute;s das normas espec&iacute;ficas contidas no diploma regulamentador do regime jur&iacute;dico do servi&ccedil;o dom&eacute;stico e em legisla&ccedil;&atilde;o complementar.</p>
    </li>
</ol>
<p style="text-align: justify"><strong>ENTIDADES REGULADORAS:</strong></p>
<p style="text-align: justify">Inspec&ccedil;&atilde;o Geral de Trablho;</p>
<p style="text-align: justify">Direc&ccedil;&atilde;o Geral dos Cuidados de Sa&uacute;de Prim&aacute;rios</p>
<p style="text-align: justify"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS: </strong>n/a</p>
<p style="text-align: justify"><strong>DIPLOMAS ALTERADOS: </strong>n/a</p>
