INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1173, 'Segurança do transporte marítimo de passageiros - Republica o DL 293/2001 de 20 de Novembro', '93', '2012', '2012-04-19', 'Organização Marítima Internacional transporte marítimo mercadorias passageiros OMI', 'O presente decreto -lei transpõe para a ordem jurídica interna a Diretiva n.º 2010/36/UE, da Comissão, de 1 de junho de 2010, que altera a Diretiva n.º 2009/45/CE, do Parlamento Europeu e do Conselho, de 6 de maio de 2009, relativa às regras e normas de segurança para os navios de passageiros.', '<p><span style="color: #003366"><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></span><br />
</span></p>
<p style="text-align: justify">1 - O presente decreto -lei &eacute; aplic&aacute;vel aos navios de passageiros e &agrave;s embarca&ccedil;&otilde;es de passageiros de alta velocidade, independentemente da bandeira que arvorem, que efetuem viagens dom&eacute;sticas em zonas mar&iacute;timas sob soberania ou jurisdi&ccedil;&atilde;o nacional e aos navios de passageiros e embarca&ccedil;&otilde;es de passageiros de alta velocidade, de bandeira nacional, que efetuem viagens dom&eacute;sticas em zonas mar&iacute;timas sob soberania ou jurisdi&ccedil;&atilde;o de outro Estado membro.</p>
<p style="text-align: justify">2 &mdash; Est&atilde;o exclu&iacute;dos do &acirc;mbito do presente decreto -lei:</p>
<div><i>a</i>) Os navios de passageiros de guerra e de transporte de tropas, bem como as embarca&ccedil;&otilde;es de alta velocidade de guerra e de transporte de tropas;</div>
<div><i>b</i>) Os navios de passageiros existentes com comprimento inferior a 24 m;</div>
<div><i>c</i>) Os navios de passageiros sem propuls&atilde;o mec&acirc;nica;</div>
<div><i>d</i>) Navios constru&iacute;dos noutros materiais que n&atilde;o o a&ccedil;o ou materiais equivalentes, n&atilde;o abrangidos pelas normas relativas &agrave;s embarca&ccedil;&otilde;es de alta velocidade [Resolu&ccedil;&atilde;o MSC.36(63) ou MSC.97(73)] ou &agrave;s embarca&ccedil;&otilde;es com sustenta&ccedil;&atilde;o din&acirc;mica [Resolu&ccedil;&atilde;o A.373(X)], ambas da Organiza&ccedil;&atilde;o Mar&iacute;tima Internacional (OMI);</div>
<div><i>e</i>) Os navios de passageiros de madeira de constru&ccedil;&atilde;o primitiva;</div>
<div><i>f</i>) Originais ou r&eacute;plicas de navios de passageiros hist&oacute;ricos projetados antes de 1965 e constru&iacute;dos predominantemente com os materiais originais;</div>
<div><i>g</i>) As embarca&ccedil;&otilde;es de recreio, inclusive as de alta velocidade, exceto se forem tripuladas e transportarem mais de 12 passageiros para fins comerciais; e</div>
<div><i>h</i>) Os navios de passageiros e embarca&ccedil;&otilde;es de passageiros de alta velocidade que operem exclusivamente em zonas portu&aacute;rias.</div>
<p style="text-align: justify"><span style="color: #003366"><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong></span></span>&nbsp;</p>
<ul>
