INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (718, 'Fixa os valores limite de emissão (VLE) aplicáveis às instalações de combustão abrangidas pelo Decreto-Lei  78/2004, de 3 de Abril', '677', '2009', '2009-06-23', 'ambiente ar emissão poluentes atmosféricos regime prevenção controlo emissões atmosfera princípios objectivos instrumentos garantia protecção recurso natural níveis aceitáveis', 'A presente portaria fixa os valores limite de emissão (VLE) aplicáveis às instalações de combustão abrangidas pelo Decreto -Lei n.º 78/2004, de 3 de Abril', '<p><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong> n/a</p>
