INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (350, 'Colocação no mercado de produtos biocidas', '332', '2007', '2007-10-09', 'safety substâncias perigosas biocidas', 'Transpõe para a ordem jurídica interna as Directivas n.ºs 2006/50/CE, da Comissão, de 29 de Maio, que altera os anexos iv-A e iv-B da Directiva n.º 98/8/CE, do Parlamento Europeu e do Conselho, de 16 de Fevereiro, e 2006/140/CE, da Comissão, de 20 de Dezembro, que altera a Directiva n.º 98/8/CE, do Parlamento Europeu e do Conselho, de 16 de Fevereiro, com o objectivo de incluir a substância activa fluoreto de sulfurilo no seu anexo I', '<p style="text-align: justify"><strong><span style="color: rgb(0,51,102)">&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </span></strong></p>
<p style="text-align: justify">O presente diploma aplica-se a Produtos biocidas, definidos por: subst&acirc;ncias activas e prepara&ccedil;&otilde;es que contenham uma ou mais subst&acirc;ncias activas, apresentadas sob a forma em que s&atilde;o fornecidas ao utilizador, que se destinam, por mecanismos qu&iacute;micos ou biol&oacute;gicos, a destruir, travar o crescimento, tornar inofensivo, evitar ou controlar de qualquer outra forma a ac&ccedil;&atilde;o de um organismo prejudicial e que se incluam num dos 23 tipos de produtos que constam da lista exaustiva do anexo V do DL 121/2002 de 3 de Maio, aditando a subst&acirc;ncia activa fluoreto de sulfurilo.</p>
<p style="text-align: justify"><strong><span style="color: rgb(0,51,102)">DIRECTIVAS EUROPEIAS RELACIONADAS: </span></strong></p>
<p style="text-align: justify"><span>Directivas n.&ordm;s 2006/50/CE, da Comiss&atilde;o, de 29 de Maio, que altera os anexos iv-A e iv-B da Directiva n.&ordm; 98/8/CE, do Parlamento Europeu e do Conselho, de 16 de Fevereiro, e 2006/140/CE, da Comiss&atilde;o, de 20 de Dezembro, que altera a Directiva n.&ordm; 98/8/CE, do Parlamento Europeu e do Conselho, de 16 de Fevereiro. </span></p>
<p style="text-align: justify"><strong><span style="color: rgb(0,51,102)">DIPLOMAS ALTERADOS: </span></strong></p>
<p style="text-align: justify"><span style="font-family: Arial">&Eacute; alterado o Anexo I e aditados os anexos IV-A e IV-B do Decreto-Lei n&ordm; 121/2002, de 3 de Maio alterado e republicado pelo DL 85/2013 de 26 de junho</span></p>
<p>&nbsp;<strong><span style="color: rgb(0,51,102)">DIPLOMAS REVOGADOS: </span></strong></p>
<p style="text-align: justify"><span>S&atilde;o revogados os anexos IV-A e IV-B do Decreto-Lei n&ordm; 121/2002, de 3 de Maio. </span></p>
