INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1307, 'Regime de comércio de licenças de emissão de gases com efeito de estufa na Comunidade', '38', '2013', '2013-03-15', 'Ambiente gases estufa', 'O presente diploma transpõe para a ordem jurídica nacional a Diretiva n.º 2003/87/CE, do  Parlamento Europeu e do Conselho, de 13 de outubro de 2003, relativa à criação de um regime de comércio de licenças de emissão de gases com efeito de estufa na Comunidade e que altera a Diretiva n.º 96/61/CE, do Conselho, alterada pela Diretiva n.º 2004/101/CE, do Parlamento Europeu e do Conselho, de 27 de outubro de 2004, no que diz respeito aos mecanismos baseados em projetos do Protocolo de Quioto, pelo Regulamento (CE) n.º 219/2009, do Parlamento Europeu e do Conselho, de 11 de março de 2009, e pela Diretiva n.º 2009/29/CE, do Parlamento Europeu e do Conselho, de 23 de abril de 2009, a fim de melhorar e alargar o regime comunitário de comércio de licenças de emissão de gases com efeito de estufa, doravante designado regime CELE.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:&nbsp;</strong><span style="color: #000000">&nbsp; </span><br />
</span></p>
<p><span style="font-family: Arial"><span style="color: #003366"><span style="color: #000000">1 - O presente diploma aplica -se &agrave;s emiss&otilde;es provenientes das atividades desenvolvidas por instala&ccedil;&otilde;es fixas, constantes do anexo II, e aos GEE identificados no anexo I, sem preju&iacute;zo do disposto no regime jur&iacute;dico da preven&ccedil;&atilde;o e controlo integrados da polui&ccedil;&atilde;o, aprovado pelo Decreto -Lei n.&ordm; 173/2008, de 26 de agosto, alterado pelo Decreto -Lei n.&ordm; 60/2012, de 14 de mar&ccedil;o.</span></span></span></p>
<p>2 - O regime do presente diploma n&atilde;o &eacute; aplic&aacute;vel &agrave;s instala&ccedil;&otilde;es ou partes de instala&ccedil;&otilde;es utilizadas para investiga&ccedil;&atilde;o, desenvolvimento e ensaio de novos produtos ou processos, bem como &agrave;s instala&ccedil;&otilde;es abrangidas que desenvolvam unicamente a atividade de combust&atilde;o e que utilizam exclusivamente biomassa, incluindo os equipamentos que utilizam combust&iacute;veis f&oacute;sseis apenas durante as situa&ccedil;&otilde;es de arranque e paragem</p>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong><span style="color: #000000">&nbsp; </span></span></p>
<p><span style="color: #003366"><span style="color: #000000">Ag&ecirc;ncia Portuguesa do Ambiente, I.P</span></span></p>
<p><span style="color: #003366"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></span></p>
<ul>
    <li>Diretiva n.&ordm; 2003/87/CE, do &nbsp;Parlamento Europeu e do Conselho, de 13 de outubro de 2003, a qual altera a Diretiva n.&ordm; 96/61/CE, do Conselho, alterada pela Diretiva n.&ordm; 2004/101/CE, do Parlamento Europeu e do Conselho, de 27 de outubro de 2004</li>
    <li>Regulamento (CE) n.&ordm; 219/2009, do Parlamento Europeu e do Conselho, de 11 de mar&ccedil;o de 2009</li>
    <li>Diretiva n.&ordm; 2009/29/CE, do Parlamento Europeu e do Conselho, de 23 de abril de 2009</li>
</ul>
<p><span style="color: #003366"><strong>DIPLOMAS ALTERADOS:</strong> </span>n/a</p>
