INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1259, 'Regime jurídico do comércio de licenças de emissão de gases com efeito de estufa', '93', '2010', '2010-07-27', 'Ambiente Gases Efeito Estufa', 'O presente decreto -lei estabelece o regime de comércio de licenças de emissão de gases com efeito de estufa das actividades da aviação, transpondo para a ordem jurídica nacional a Directiva n.º 2008/101/CE, do Parlamento Europeu e do Conselho, de 19 de Novembro de 2008, que altera a Directiva n.º 2003/87/CE, do Parlamento Europeu e do Conselho, de 13 de Outubro de 2003, de modo a incluir as actividades da aviação no regime de comércio de licenças de emissão de gases com efeito de estufa.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p>O presente decreto -lei aplica -se aos operadores de aeronaves:</p>
<p><span id="1357769337982S" style="display: none">&nbsp;</span><i>a</i>) Titulares de uma licen&ccedil;a de explora&ccedil;&atilde;o v&aacute;lida emitida pelo Instituto Nacional de Avia&ccedil;&atilde;o Civil, I. P. (INAC, I. P.), em conformidade com as disposi&ccedil;&otilde;es do Regulamento (CE) n.&ordm; 1008/2008, do Parlamento Europeu e do Conselho, de 24 de Setembro, relativo a regras comuns de explora&ccedil;&atilde;o&nbsp;<span id="1357769338080E" style="display: none">&nbsp;</span>dos servi&ccedil;os a&eacute;reos na Comunidade e que constem da lista de operadores de aeronave; ou</p>
<div><i>b</i>) Que n&atilde;o disponham de licen&ccedil;a de explora&ccedil;&atilde;o v&aacute;lida ou que possuam uma licen&ccedil;a de explora&ccedil;&atilde;o proveniente de pa&iacute;ses terceiros e cuja estimativa mais elevada de emiss&otilde;es em rela&ccedil;&atilde;o aos voos realizados por esse operador no ano de base sejam atribu&iacute;veis ao Estado Portugu&ecirc;s, desde que constem da lista de operadores de aeronave.</div>
<div>&nbsp;</div>
