INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (253, 'Alteração ao Código do Trabalho parcialmente revogado pela Lei 7/2009 de 12 de Fevereiro', '9', '2006', '2006-03-20', 'safety SHT segurança higiene trabalho código negociação contratação colectiva', 'Altera o Código do Trabalho, aprovado pela Lei n.º 99/2003, de 27 de Agosto, e a respectiva regulamentação, aprovada pela Lei n.º 35/2004, de 29 de Julho, em matérias relativas a negociação e contratação colectiva', '<p><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong>  n/a</p>
