INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1403, 'Quadro comunitário para a segurança nuclear das instalações nucleares', '30', '2012', '2012-02-09', 'Segurança Nuclear', 'O presente diploma transpõe para a ordem jurídica interna as disposições da Diretiva n.º 2009/71/EURATOM, do Conselho, de 25 de junho de 2009, que estabelece um quadro comunitário para a segurança nuclear das instalações nucleares, e cria a respetiva autoridade reguladora competente, estabelecendo o seu âmbito e atribuições.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong><span style="color: #000000">&nbsp; </span><br />
</span></p>
<p><span style="color: #003366"><span style="color: #000000">1 &mdash; O presente diploma aplica -se a qualquer instala&ccedil;&atilde;o nuclear civil explorada sob uma licen&ccedil;a, tal como definida na al&iacute;nea b) do artigo 3.&ordm;, em todas as fases abrangidas por essa licen&ccedil;a, bem como &agrave; regula&ccedil;&atilde;o das atividades dessas instala&ccedil;&otilde;es. <br />
2 &mdash; S&atilde;o seus objetivos:<br />
a) A preserva&ccedil;&atilde;o e a promo&ccedil;&atilde;o da melhoria cont&iacute;nua da seguran&ccedil;a nuclear e da sua regula&ccedil;&atilde;o;<br />
b) A ado&ccedil;&atilde;o de disposi&ccedil;&otilde;es conducentes a um elevado n&iacute;vel de seguran&ccedil;a nuclear, que proteja os trabalhadores e a popula&ccedil;&atilde;o em geral dos riscos de contamina&ccedil;&atilde;o nuclear.</span></span></p>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong><span style="color: #000000"> </span></span></p>
