INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (298, 'Prevenção da exposição dos trabalhadores e do público a radiações ionizantes resultantes de um controlo inadequado das fontes radioactivas seladas', '38', '2007', '2007-02-19', 'safety SHT segurança higiene trabalho radiações ionizantes radioactivas electromagnéticas campos electromagnéticos', 'Estabelece o regime jurídico da prevenção da exposição dos trabalhadores e do público a radiações ionizantes resultantes de um controlo inadequado das fontes radioactivas seladas e transpõe a Directiva n.o 2003/122/EURATOM, do Conselho, de 22 de Dezembro.
