INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1548, 'DLR 6/2015/A - Regime jurídico de segurança contra incêndio em edifícios na Região Autónoma dos Açores', '6/A', '2015', '2015-03-05', 'safety , SHT , segurança , higiene , trabalho , incêndios , SCI , RTSCI , edifícios', 'O presente diploma estabelece o regime jurídico de segurança contra incêndio em edifícios na Região Autónoma dos Açores, abreviadamente designado por SCIEA.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O: </strong><br />
</span></p>
<p><span style="color: #000000">1 &mdash; Est&atilde;o sujeitos ao SCIEA:<br />
a) Os edif&iacute;cios, ou suas fra&ccedil;&otilde;es aut&oacute;nomas, qualquer que seja a utiliza&ccedil;&atilde;o e respetiva envolvente;<br />
b) Os edif&iacute;cios de apoio a postos de abastecimento de combust&iacute;veis, tais como estabelecimentos de restaura&ccedil;&atilde;o, comerciais e oficinas, regulados pelos Decretos -Lei n.os 302/2001, de 23 de novembro, e 267/2002, de 26 de novembro, alterado pelos Decretos -Lei n.os 389/2007, de 30 de novembro, 31/2008, de 25 de fevereiro, 195/2008, de 6 de outubro, e 217/2012, de 9 de outubro; <br />
c) Os recintos. <br />
As excep&ccedil;&otilde;es devem ser consultadas no diploma (artigo 5&ordm;). <br />
As medidas de autoprotec&ccedil;&atilde;o s&atilde;o aplic&aacute;veis a todos os edif&iacute;cios e recintos, incluindo os existentes.</span></p>
<p><span style="color: #003366"><strong>ENTIDADES REGULADORAS:</strong><span style="color: #000000"> </span></span></p>
<ul>
    <li><span style="color: #003366"><span style="color: #000000">1 &mdash; O Servi&ccedil;o Regional de Prote&ccedil;&atilde;o Civil e Bombeiros dos A&ccedil;ores, abreviadamente designado por SRPCBA &eacute; a entidade competente para assegurar o cumprimento do SCIEA. </span></span></li>
    <li><span style="color: #003366"><span style="color: #000000">2 &mdash; Ao SRPCBA incumbe a credencia&ccedil;&atilde;o de entidades para a emiss&atilde;o de pareceres, a realiza&ccedil;&atilde;o de vistorias e de inspe&ccedil;&otilde;es das condi&ccedil;&otilde;es de SCIEA, nos termos previstos no presente diploma. </span></span></li>
</ul>
