INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (1552, 'Política de Ordenamento e de Gestão do Espaço Marítimo Nacional', '38', '2015', '2015-03-12', 'Ambiente ordenamento marítimo', '1 — O presente decreto -lei desenvolve a Lei n.º 17/2014, de 10 de abril, que estabelece as Bases da Política de Ordenamento e de Gestão do Espaço Marítimo Nacional (LBOGEM), definindo: 
a) O regime de elaboração, aprovação, alteração, revisão e suspensão dos instrumentos de ordenamento do espaço marítimo nacional; 
b) O regime jurídico aplicável aos títulos de utilização privativa do espaço marítimo nacional;
c) O regime económico e financeiro associado à utilização privativa do espaço marítimo nacional;
d) O regime de acompanhamento permanente e de avaliação técnica do ordenamento do espaço marítimo nacional;
e) O regime de utilização privativa dos recursos hídricos em águas de transição para fins aquícolas.
2 — O presente decreto -lei também transpõe para a ordem jurídica interna a Diretiva n.º 2014/89/UE, do Parlamento Europeu e do Conselho, de 23 de julho de 2014, que estabelece um quadro para o ordenamento do espaço marítimo.', '<p><span style="color: #003366"><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong>&nbsp; <br />
</span></p>
<p><span style="color: #003366"><span style="color: #000000">1 &mdash; O presente decreto -lei aplica -se ao espa&ccedil;o mar&iacute;timo nacional, tal como definido na LBOGEM.<br />
2 &mdash; O presente decreto -lei aplica -se ainda &agrave;s &aacute;guas de transi&ccedil;&atilde;o no que respeita apenas &agrave; utiliza&ccedil;&atilde;o privativa dos recursos h&iacute;dricos para fins aqu&iacute;colas.<br />
3 &mdash; O presente decreto -lei n&atilde;o se aplica &agrave;s &aacute;reas sob jurisdi&ccedil;&atilde;o das entidades portu&aacute;rias.</span></span></p>
<p><span style="color: #003366"><strong>DIRECTIVAS EUROPEIAS RELACIONADAS:</strong></span></p>
<p>Diretiva n.&ordm; 2014/89/UE, do Parlamento Europeu e do Conselho, de 23 de julho de 2014</p>
