INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (754, 'Regulamenta o regime de reparação de acidentes de trabalho e de doenças profissionais, incluindo a reabilitação e reintegração profissionais', '98', '2009', '2009-09-04', 'Safety SHT código trabalho acidentes trabalho doenças profissionais', 'Regulamenta o regime de reparação de acidentes de trabalho e de doenças profissionais, incluindo a reabilitação e reintegração profissionais, nos termos do artigo 284.º do Código do Trabalho, aprovado pela Lei n.º 7/2009, de 12 de Fevereiro', '<p style="text-align: justify; "><strong>&Acirc;MBITO DE APLICA&Ccedil;&Atilde;O:</strong></p>
