INSERT INTO public.legaldocument (id, customtitle, number, year, publicationdate, keywords, summary, customabstract, transitoryprovisions, creationdate, statechangeddate, sendnotificationnew, sendnotificationchange, published, changedby_id, documenttype_id, documentstate_id, filename, legalcompliancevalidation, referencearticles) VALUES (254, 'Regulamenta o Código do Trabalho parcialmente revogado pela Lei 7/2009 de 12 de Fevereiro', '35', '2004', '2004-07-29', 'safety SHT segurança higiene trabalho código', 'Regulamenta a Lei nº 99/2003, de 27 de Agosto, que aprovou o código do trabalho. O regime previsto na presente Lei aplica-se aos contratos de trabalho regulados pelo Código do Trabalho, bem como aos contratos com regime especial relativamente às normas que não sejam incompatíveis com a especificidade destes, sem prejuízo do âmbito de aplicação de cada capítulo. A presente Lei aplica-se ainda à relação jurídica de emprego público, nos termos do artigo 5.º da Lei n.º 99/2003, de 27 de Agosto', '<p><strong>ENTIDADES REGULADORAS:</strong></p>
