INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (2, 'Nova aplicação no firmamento português', '2009-04-16 10:49:58.497000', 'Certitools, uma nova aplicação produzida por uma pequena empresa da rua de Moçambique, passa a ser pré-instalada no computador Magalhães que o PM se prepara para distribuir na proxima visita à Antartida', false, 1);
INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (1, 'A Lua acusada de conservadorismo', '2009-04-16 10:26:28.498000', 'Rama Calhau, em discurso, acusa a Lua de não inovar a órbita, contribuindo para manter a crise.
