INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (10, 'Módulo PEI já integra Medidas de Autoprotecção - artigo 21º do DL 220/2008', '2009-05-12 12:05:32.642000', 'O módulo Plano de Emergência do CertiTools já integra todas as Medidas de Autoprotecção referidas no artigo 21º do DL 220/2008, no sentido destas serem facilmente auditáveis, destacando-se a possibilidade de incluir, entre outros, os seguintes registos de segurança (medidas de autoprotecção do artigo 201º da Portaria 1532/2008 de 29 de Dezembro):

• Relatórios de vistoria
• Registo de anomalias
• Registos de acções de manutenção
• Registos modificações / alterações da instalação
• Registos de realização de trabalhos perigosos
• Registos de ocorrências de segurança contra incêndios
• Relatórios de intervenção de entidades externas
• Relatórios / registos de acções de formação
• Registos de simulacros', true, 1);
