INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (17, 'Certitools ultrapassa os 4000 utilizadores', '2010-02-24 17:10:50.386000', '<p>J&aacute; s&atilde;o mais de 4000 os utilizadores que t&ecirc;m acesso e navegam no Certitools, acedem aos seus Planos de Emerg&ecirc;ncia e Planos de Preven&ccedil;&atilde;o.&nbsp;Uma faceta da&nbsp;seguran&ccedil;a&nbsp; agora muito mais acess&iacute;vel&nbsp;na vida di&aacute;ria destas entidades.&nbsp;</p>
<p>Acessibilidade e Simplicidade&nbsp;&nbsp;- dois objectivos Certitools agora partilhados por muitos.</p>', true, 1);
