INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (11, 'Plano de Prevenção - Um módulo do Certitools', '2009-05-12 12:09:21.352000', '<p>PLANO DE PREVEN&Ccedil;&Atilde;O: um dos m&oacute;dulos do CertiTools em desenvolvimento que vem dar resposta &agrave; necessidade de garantir uma integra&ccedil;&atilde;o total de todos os documentos que constituem o Plano de Seguran&ccedil;a Interno (Plano de Emerg&ecirc;ncia Interno, Plano de Preven&ccedil;&atilde;o e Medidas de Autoprotec&ccedil;&atilde;o) conforme referido no Artigo 21&ordm; do novo Regime jur&iacute;dico da Seguran&ccedil;a Contra Inc&ecirc;ndios.</p>', true, 1);
INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (15, 'Nova Release do Certitools', '2010-02-23 17:17:26.392000', '<p>Foi instalada uma nova release do Certitools. Entre outras funcionalidades, foi dado destaque &agrave; vertente internacional do Certitools,&nbsp;facilitando assim a&nbsp;navega&ccedil;&atilde;o na aplica&ccedil;&atilde;o por utilizadores que dominam outras linguas que n&atilde;o a portuguesa.&nbsp;&nbsp;</p>', true, 1);
INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (18, 'Over 4000 Certitools Users', '2010-02-24 17:24:09.621000', '<p><span id="result_box" class="long_text"><span title="J&aacute; s&atilde;o mais de 4000 os utilizadores que t&ecirc;m acesso e navegam no Certitools, acedem aos seus Planos de Emerg&ecirc;ncia e Planos de Preven&ccedil;&atilde;o." onmouseout="this.style.backgroundColor=''#fff''" onmouseover="this.style.backgroundColor=''#ebeff9''" style="background-color: #fff">There&nbsp;are&nbsp;more than&nbsp;4000 users who&nbsp;now access and navigate in Certitools, access their Emergency Plans and Prevention Plans.&nbsp;</span><span title="Uma faceta da seguran&ccedil;a agora muito mais acess&iacute;vel na vida di&aacute;ria destas entidades." onmouseout="this.style.backgroundColor=''#fff''" onmouseover="this.style.backgroundColor=''#ebeff9''" style="background-color: #fff">A&nbsp;safety area now much more accessible in these entities daily life.<br />
<br />
