INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (16, 'Lauched a new Release in Certitools', '2010-02-23 18:05:50.779000', '<p>A new release has been launched. Among other functionalities, we highlight the international area, where making it&nbsp;easier to navigate and explore the application&nbsp;by all those users that master other languages but not&nbsp;Portuguese has been an important target.</p>', true, 2);
INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (19, 'Nova release  do Certitools', '2010-04-08 00:00:00.000000', '<p>Foi instalada uma nova release do Certitools. Porque o feedback dos nossos&nbsp;clientes &eacute; muito importante para n&oacute;s, as novas funcionalidades introduzidas s&atilde;o produto desse feedback em conjunto com uma an&aacute;lise de melhoria cont&iacute;nua a que regularmente nos propomos.</p>', true, 1);
INSERT INTO public.news (id, title, creationdate, content, published, category_id) VALUES (20, 'New release lauched in Certitools', '2010-04-08 00:00:00.000000', '<p>A new release has been lauched. Our clients''&nbsp;feedback is of extreme importance to us. The new functionalities are the result of that feedback in conjunction with our analysis for continuous&nbsp;improvement<span id="result_box" class="medium_text"><span title="s&atilde;o produto desse feedback em conjunto com uma an&aacute;lise de melhoria cont&iacute;nua a que regularmente nos propomos." onmouseout="this.style.backgroundColor=''#fff''" onmouseover="this.style.backgroundColor=''#ebeff9''" style="background-color: #fff"><font size="3">.</font></span></span></p>', true, 2);
